mapper.insert:
Insert into CODE (
DIVISION_CODE_NO
, CODE_TYPE
, DIVISION_CODE_NAME
, CODE_CHANGE_AVAILABLE
, DESCRIPTION
) VALUES (
#{divisionCodeNo}
, #{codeType}
, #{divisionCodeName}
, #{codeChangeAvailable}
, #{description} 
)

mapper.update:
  	  	UPDATE CODE SET
		CODE_TYPE = #{codeType}
		, DIVISION_CODE_NAME = #{divisionCodeName}
		, CODE_CHANGE_AVAILABLE = #{codeChangeAvailable} 
		, DESCRIPTION = #{description}
		WHERE DIVISION_CODE_NO =#{divisionCodeNo}
